CREATE OR REPLACE PROCEDURE HrmProvince_Insert(provincename_1 varchar2, provincedesc_2 varchar2, countryid_3    integer, flag           out integer, msg            out varchar2, thecursor      IN OUT cursor_define.weavercursor) as maxid integer; begin select (nvl(max(id),0)) into maxid from HrmProvince; maxid := maxid + 1; insert into HrmProvince (id, provincename, provincedesc, countryid) VALUES (maxid, provincename_1, provincedesc_2, countryid_3); open thecursor for select max(id) from HrmProvince; end;
/
CREATE OR REPLACE PROCEDURE HrmCityTwo_Insert(cityname_1      varchar2, citylongitude_1 number, citylatitude_1  number, cityid_1        integer, flag            out integer, msg             out varchar2, thecursor       IN OUT cursor_define.weavercursor) as citypid_1 integer; begin select (NVL(max(id),0) + 1) into citypid_1 from HrmCityTwo; insert into HrmCityTwo (id, cityname, citylongitude, citylatitude, cityid) VALUES (citypid_1, cityname_1, citylongitude_1, citylatitude_1, cityid_1); open thecursor for select citypid_1 from HrmCityTwo; end;
/
CREATE OR REPLACE PROCEDURE HrmCity_Insert (cityname_1 	varchar2, citylongitude_1 number, citylatitude_1 number, provinceid_1 integer, countryid_1 integer, flag out integer, msg out varchar2, thecursor IN OUT cursor_define.weavercursor ) as cityid_1 integer ; begin select (nvl(max(id),0)+1 ) into cityid_1 from HrmCity ; insert into HrmCity ( id , cityname, citylongitude, citylatitude, provinceid, countryid ) VALUES ( cityid_1 , cityname_1, citylongitude_1, citylatitude_1, provinceid_1, countryid_1); open thecursor for select cityid_1 from  HrmCity; end;
