CREATE OR REPLACE PROCEDURE HRMSALARYPAYDETAIL_UPDATE (payid_1         integer, itemid_2       varchar2, hrmid_3       integer, salary_4       number, departmentid_5	integer, flag    out integer, msg out varchar2, thecursor IN OUT cursor_define.weavercursor)  AS count_1 integer; begin select count(payid) into count_1 from HrmSalaryPaydetail where ( payid    = payid_1 AND itemid = itemid_2 AND hrmid = hrmid_3 AND departmentid = departmentid_5); if count_1 is null or count_1 = 0 then INSERT INTO HrmSalaryPaydetail ( payid, itemid, hrmid, salary, departmentid) VALUES ( payid_1, itemid_2, hrmid_3, salary_4, departmentid_5); else UPDATE HrmSalaryPaydetail SET  salary=salary_4, departmentid=departmentid_5 WHERE ( payid  = payid_1 AND itemid    = itemid_2 AND hrmid    = hrmid_3) ; end if; end;
/