CREATE PROCEDURE Doc_Sec_Insert_for_safe (@subcategoryid int, @categoryname varchar(200),
                                                                            @docmouldid int, @publishable char(1),
                                                                                                          @replyable char(1),
                                                                                                                     @shareable char(1),
                                                                                                                                @cusertype int, @cuserseclevel tinyint, @cdepartmentid1 int, @cdepseclevel1 tinyint, @cdepartmentid2 int, @cdepseclevel2 tinyint, @croleid1 int, @crolelevel1 char(1),
                                                                                                                                                                                                                                                                                              @croleid2 int, @crolelevel2 char(1),
                                                                                                                                                                                                                                                                                                                          @croleid3 int, @crolelevel3 char(1),
                                                                                                                                                                                                                                                                                                                                                      @hasaccessory char(1),
                                                                                                                                                                                                                                                                                                                                                                    @accessorynum tinyint, @hasasset char(1),
                                                                                                                                                                                                                                                                                                                                                                                                     @assetlabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                 @hasitems char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                           @itemlabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                                      @hashrmres char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                 @hrmreslabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                                                              @hascrm char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                      @crmlabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                @hasproject char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            @projectlabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          @hasfinance char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      @financelabel varchar(200),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    @approveworkflowid int, @markable char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      @markAnonymity char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     @orderable char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                @defaultLockedDoc int, @allownModiMShareL int, @allownModiMShareW int, @maxUploadFileSize int, @wordmouldid int, @isSetShare int, @noDownload int, @noRepeatedName int, @isControledByDir int, @pubOperation int, @childDocReadRemind int, @readOpterCanPrint int, @isLogControl char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 @uploadext varchar(4000),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            @subcompanyId int, @level int, @parentid int, @secorder float, @flag int OUTPUT,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     @msg varchar(80) OUTPUT) AS DECLARE @dirid int DECLARE @dirtype int IF @level=1 BEGIN
SET @dirtype = 0
INSERT INTO DocMainCategory(categoryname,categoryiconid,categoryorder,coder,norepeatedname,subcompanyid)
VALUES(@categoryname,
       0,
       @secorder,
       0,
       @noRepeatedName,
       @subcompanyId) DECLARE maindir_cursor
CURSOR
FOR
SELECT MAX(id)
FROM DocMainCategory
WHERE categoryname=@categoryname OPEN maindir_cursor FETCH NEXT
  FROM maindir_cursor INTO @dirid CLOSE maindir_cursor DEALLOCATE maindir_cursor END ELSE BEGIN IF @level=2 BEGIN
SET @dirtype = 1
INSERT INTO DocSubCategory(categoryname,maincategoryid,subcategoryid,suborder,norepeatedname)
SELECT @categoryname,
       dirid,
       -1,
       @secorder,
       @noRepeatedName
FROM DocSecCategory
WHERE id=@parentid DECLARE subdir_cursor
  CURSOR
  FOR
  SELECT MAX(id)
  FROM DocSubCategory WHERE categoryname=@categoryname OPEN subdir_cursor FETCH NEXT
  FROM subdir_cursor INTO @dirid CLOSE subdir_cursor DEALLOCATE subdir_cursor END END
INSERT INTO docseccategory(subcategoryid,
                           categoryname,
                           docmouldid,
                           publishable,
                           replyable,
                           shareable,
                           cusertype,
                           cuserseclevel,
                           cdepartmentid1,
                           cdepseclevel1,
                           cdepartmentid2,
                           cdepseclevel2,
                           croleid1,
                           crolelevel1,
                           croleid2,
                           crolelevel2,
                           croleid3,
                           crolelevel3,
                           hasaccessory,
                           accessorynum,
                           hasasset,
                           assetlabel,
                           hasitems,
                           itemlabel,
                           hashrmres,
                           hrmreslabel,
                           hascrm,
                           crmlabel,
                           hasproject,
                           projectlabel,
                           hasfinance,
                           financelabel,
                           approveworkflowid,
                           markable,
                           markAnonymity,
                           orderable,
                           defaultLockedDoc,
                           allownModiMShareL,
                           allownModiMShareW,
                           maxUploadFileSize,
                           wordmouldid,
                           isSetShare,
                           nodownload,
                           norepeatedname,
                           iscontroledbydir,
                           puboperation,
                           childdocreadremind,
                           readoptercanprint,
                           isLogControl,
                           uploadext,
                           subcompanyId,
                           parentid,
                           dirid,
                           dirType)
VALUES(@subcategoryid,
       @categoryname,
       @docmouldid,
       @publishable,
       @replyable,
       @shareable,
       @cusertype,
       @cuserseclevel,
       @cdepartmentid1,
       @cdepseclevel1,
       @cdepartmentid2,
       @cdepseclevel2,
       @croleid1,
       @crolelevel1,
       @croleid2,
       @crolelevel2,
       @croleid3,
       @crolelevel3,
       @hasaccessory,
       @accessorynum,
       @hasasset,
       @assetlabel,
       @hasitems,
       @itemlabel,
       @hashrmres,
       @hrmreslabel,
       @hascrm,
       @crmlabel,
       @hasproject,
       @projectlabel,
       @hasfinance,
       @financelabel,
       @approveworkflowid,
       @markable,
       @markAnonymity,
       @orderable,
       @defaultLockedDoc,
       @allownModiMShareL,
       @allownModiMShareW,
       @maxUploadFileSize,
       @wordmouldid,
       @isSetShare,
       @noDownload,
       @noRepeatedName,
       @isControledByDir,
       @pubOperation,
       @childDocReadRemind,
       @readOpterCanPrint,
       @isLogControl,
       @uploadext,
       @subcompanyId,
       @parentid,
       @dirid,
       @dirtype)
SELECT max(id)
FROM docseccategory
WHERE categoryname = @categoryname
GO
CREATE PROCEDURE Doc_Sec_Update_for_safe (@id int, @subcategoryid [int],
                                                       @categoryname [varchar](200),
                                                                     @coder [varchar](20),
                                                                            @docmouldid [int],
                                                                            @publishable [char](1),
                                                                                         @replyable [char](1),
                                                                                                    @shareable [char](1),
                                                                                                               @cusertype [int],
                                                                                                               @cuserseclevel [tinyint],
                                                                                                               @cdepartmentid1 [int],
                                                                                                               @cdepseclevel1 [tinyint],
                                                                                                               @cdepartmentid2 [int],
                                                                                                               @cdepseclevel2 [tinyint],
                                                                                                               @croleid1 [int],
                                                                                                               @crolelevel1 [char](1),
                                                                                                                            @croleid2 [int],
                                                                                                                            @crolelevel2 [char](1),
                                                                                                                                         @croleid3 [int],
                                                                                                                                         @crolelevel3 [char](1),
                                                                                                                                                      @hasaccessory [char](1),
                                                                                                                                                                    @accessorynum [tinyint],
                                                                                                                                                                    @hasasset [char](1),
                                                                                                                                                                              @assetlabel [varchar](200),
                                                                                                                                                                                          @hasitems [char](1),
                                                                                                                                                                                                    @itemlabel [varchar](200),
                                                                                                                                                                                                               @hashrmres [char](1),
                                                                                                                                                                                                                          @hrmreslabel [varchar](200),
                                                                                                                                                                                                                                       @hascrm [char](1),
                                                                                                                                                                                                                                               @crmlabel [varchar](200),
                                                                                                                                                                                                                                                         @hasproject [char](1),
                                                                                                                                                                                                                                                                     @projectlabel [varchar](200),
                                                                                                                                                                                                                                                                                   @hasfinance [char](1),
                                                                                                                                                                                                                                                                                               @financelabel [varchar](200),
                                                                                                                                                                                                                                                                                                             @approveworkflowid int, @markable [char](1),
                                                                                                                                                                                                                                                                                                                                               @markAnonymity [char](1),
                                                                                                                                                                                                                                                                                                                                                              @orderable [char](1),
                                                                                                                                                                                                                                                                                                                                                                         @defaultLockedDoc int , @allownModiMShareL int, @allownModiMShareW int, @maxUploadFileSize int, @wordmouldid int, @isSetShare int, @noDownload int, @noRepeatedName int, @isControledByDir int, @pubOperation int, @childDocReadRemind int, @readOpterCanPrint int, @isLogControl char(1),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           @uploadext varchar(4000),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      @flag int OUTPUT,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                @msg varchar(80) OUTPUT) AS
  UPDATE docseccategory
  SET subcategoryid=@subcategoryid,
      categoryname=@categoryname,
      coder=@coder,
      docmouldid=@docmouldid,
      publishable=@publishable,
      replyable=@replyable,
      shareable=@shareable,
      cusertype=@cusertype,
      cuserseclevel=@cuserseclevel,
      cdepartmentid1=@cdepartmentid1,
      cdepseclevel1=@cdepseclevel1,
      cdepartmentid2=@cdepartmentid2,
      cdepseclevel2=@cdepseclevel2,
      croleid1=@croleid1,
      crolelevel1=@crolelevel1,
      croleid2=@croleid2,
      crolelevel2=@crolelevel2,
      croleid3=@croleid3,
      crolelevel3=@crolelevel3,
      approveworkflowid=@approveworkflowid,
      hasaccessory=@hasaccessory,
      accessorynum=@accessorynum,
      hasasset=@hasasset,
      assetlabel=@assetlabel,
      hasitems=@hasitems,
      itemlabel=@itemlabel,
      hashrmres=@hashrmres,
      hrmreslabel=@hrmreslabel,
      hascrm=@hascrm,
      crmlabel=@crmlabel,
      hasproject=@hasproject,
      projectlabel=@projectlabel,
      hasfinance=@hasfinance,
      financelabel=@financelabel,
      markable=@markable,
      markAnonymity=@markAnonymity,
      orderable=@orderable,
      defaultLockedDoc=@defaultLockedDoc,
      allownModiMShareL=@allownModiMShareL,
      allownModiMShareW=@allownModiMShareW,
      maxUploadFileSize=@maxUploadFileSize,
      wordmouldid=@wordmouldid,
      isSetShare=@isSetShare,
      nodownload=@noDownload,
      norepeatedname=@noRepeatedName,
      iscontroledbydir=@isControledByDir,
      puboperation=@pubOperation,
      childdocreadremind=@childDocReadRemind,
      readoptercanprint=@readOpterCanPrint,
      isLogControl=@isLogControl,
      uploadext=@uploadext WHERE id=@id
      GO