ALTER procedure [HrmSalaryPaydetail_Update] (@payid_1        int, @itemid_2  varchar(4000), @hrmid_3         int, @salary_4  decimal(10,2), @departmentid_5	int,@flag          integer output, @msg           varchar(4000) output)  AS declare @count int  select @count = count(payid) from HrmSalaryPaydetail where ( payid    = @payid_1 AND itemid   = @itemid_2 AND hrmid   = @hrmid_3 AND departmentid = @departmentid_5)  if @count is null or @count = 0 INSERT INTO HrmSalaryPaydetail ( payid, itemid, hrmid, salary, departmentid)  VALUES ( @payid_1, @itemid_2, @hrmid_3, @salary_4, @departmentid_5) else UPDATE HrmSalaryPaydetail SET  salary      = @salary_4, departmentid = @departmentid_5  WHERE ( payid       = @payid_1 AND itemid   = @itemid_2 AND hrmid   = @hrmid_3)
GO
