function.example:

var result = Mobile_NS.SQL("insert into person(id,name,sex) values('123','张三','男')");
alert(result);  // true | false (执行成功 true, 执行失败 false)

var result = Mobile_NS.SQL("select id, name from person where id='123'");
alert(result[0].name);  // "张三"

Mobile_NS.SQL("select id, name from person where id='123'", function(result){
	alert(result[0].name);  // "张三"
});

Mobile_NS.SQL("select id, name from person where id='123'", "datasource", function(result){
	alert(result[0].name);  // "张三"
});

条件中带页面参数
Mobile_NS.SQL("select id, name from person where id='{personid}'", function(result){
    alert(result[0].name);  // "张三"
});

条件中带变量
var personid = $("input[name='fieldname_personid']").val();
Mobile_NS.SQL("select id, name from person where id='{personid}';personid="+personid, function(result){
    alert(result[0].name);  // "张三"
});

function.example_en:

var result = Mobile_NS.SQL("insert into person(id,name,sex) values('123','Jason','male')");
alert(result);  // true | false (success: true, fail: false)

var result = Mobile_NS.SQL("select id, name from person where id='123'");
alert(result[0].name);  // "Jason"

Mobile_NS.SQL("select id, name from person where id='123'", function(result){
	alert(result[0].name);  // "Jason"
});

Mobile_NS.SQL("select id, name from person where id='123'", "datasource", function(result){
	alert(result[0].name);  // "Jason"
});

Conditions with page parameters:
Mobile_NS.SQL("select id, name from person where id='{personid}'", function(result){
    alert(result[0].name);  // "Jason"
});

Conditions with variable:
var personid = $("input[name='fieldname_personid']").val();
Mobile_NS.SQL("select id, name from person where id='{personid}';personid="+personid, function(result){
    alert(result[0].name);  // "Jason"
});

function.example_hk:

